#include <stdio.h>
#include <stdlib.h>
#include <time.h>

/*
 * A Terminal Based Game Example Program
 * The game's objective is to guess a random number generated by the program
 * within a specific number of tries
 */

int main() {
    // Declare necessary variables
    int guess, number, tries = 0, maxTries = 10, start = 1, end = 100;

    // Seed the random number generator with the current time
    srand(time(NULL));

    // Generate a random number between start and end
    number = (rand() % (end - start + 1)) + start;

    // Welcome the player to the game
    printf("Welcome to the Number Guessing Game!\n");
    printf("----------------------------------\n");
    printf("You have %d tries to guess a number between %d and %d\n", maxTries, start, end);

    // Prompt the player to enter their first guess
    printf("Enter your first guess: ");
    scanf("%d", &guess);

    // Loop until the player either guesses the correct number or runs out of tries
    while (guess != number && tries < maxTries) {
        // Give the player a hint based on whether their guess was too high or too low
        if (guess < number) {
            printf("Too low! ");
        } else {
            printf("Too high! ");
        }

        // Prompt the player to enter their next guess
        printf("Enter your next guess: ");
        scanf("%d", &guess);

        // Increment the number of tries
        tries++;
    }

    // Check whether the while loop was exited due to guessing the correct number or running out of tries
    if (guess == number) {
        printf("Congratulations! You guessed the correct number in %d tries!\n", tries + 1);
    } else {
        printf("Sorry, you ran out of tries. The number was %d.\n", number);
    }

    // Exit the program
    return 0;
}