#include <stdio.h>
#include <stdlib.h>

int main() {
  int num_values = 10;
  int values[num_values];

  // generate random numbers using C
  for (int i = 0; i < num_values; i++) {
    values[i] = rand() % 100;
  }

  // print the list of numbers generated by C
  printf("C-generated random numbers:\n");
  for (int i = 0; i < num_values; i++) {
    printf("%d ", values[i]);
  }
  printf("\n");

  // pass the list of numbers to a Python script for further processing
  char command[100];
  snprintf(command, sizeof command, "python3 process_numbers.py %d %d %d %d %d %d %d %d %d %d", 
          values[0], values[1], values[2], values[3], values[4], 
          values[5], values[6], values[7], values[8], values[9]);
  system(command);

  return 0;
}